package github;

public class While19 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		        int i=0,count=0;
		        while(count!=5)
		        {
		        i++;
		          if((i%2==0) && (i%3==0) && (i%5==0))
		          {
		         System.out.println(i);
		         count=count+1;
		          }
		        }
		}
}
